class ParameterTextfield extends BaseParameterTextfield
{
	public function getEditForm()
	{

		if( $this->editForm == null )
			$this->editForm = new ParameterTextfieldForm( $this );
			
		return $this->editForm;
	}
	
  public function createAndGetParamValueForm( $product = null ){
  	
  	$this->setParamValueForm( new ParameterProductValueTextFieldForm( 
  					Doctrine::getTable('ParameterProductValue')
  						->getObjectForProductAndParameter( $product, $this ) ) );
  						
  	return $this->getParamValueForm();

  	
  }
  

	

}